Alias.script:
local commands = {

  start = function()
    tfe.mapping_start()
  end,

  stop = function ()
    tfe.mapping_stop()
  end,

  area = function(name)
    setAreaName(tfe.roomInfo.areaId, name)
  end,

}

if (commands[matches[2]]) then
  commands[matches[2]](matches[3])
else
  cecho('<red>TFE MAP: <white>unknown command - ' .. matches[2] .. '\n\n')
end

local commands = {
  stop = tfe.speedwalkStop,
  pause = tfe.speedwalkPause,
  resume = tfe.speedwalkResume,
}

commands[matches[2]]()
